swipe_speed=400;$(document).ready(function(){swipe_prepare_popular_products();swipe_prepare_discounts()});function swipe_prepare_popular_products(){var a=new Swipe(document.getElementById("popular_slide"),{speed:swipe_speed,auto:4000,callback:function(b,c){var d=$("#box_popular").find(".nav li");d.removeClass("previous").removeClass("selected");d.eq(b-1).addClass("previous");d.eq(b).addClass("selected");copy_attribute(b,"href");copy_attribute(b,"title");copy_text(b)}});swipe_prepare_arrows("box_popular",a);$("#box_popular").find(".nav a").each(function(b,c){$(this).click(function(d){d.preventDefault();a.delay=0;clearTimeout(a.interval);a.slide(b,swipe_speed)})});$("#box_popular").hover(function(){a.pause()},function(){a.resume()});return a}function swipe_prepare_discounts(){var a=new Swipe(document.getElementById("discounts_slide"),{speed:swipe_speed});swipe_prepare_arrows("box_discounts",a)}function swipe_prepare_arrows(b,a){$("#"+b+" .prev").click(function(){a.prev()});$("#"+b+" .next").click(function(){a.next()})}function copy_attribute(a,b){box_popular_more_link().attr(b,box_popular_nav_link(a).attr(b))}function copy_text(a){box_popular_more_link().fadeOut(function(){$(this).text(translations.see_all_in+" "+box_popular_nav_link(a).text()).fadeIn()})}function box_popular_nav_link(a){return $("#box_popular .nav li a").eq(a)}function box_popular_more_link(){return $("#box_popular .more")};